/**
 * 辅助工具，输出彩色控制台信息。
 * @packageDocumentation
 */
/**
 * 日志工具类
 */
export default class Logger {
    prefix: string;
    /**
     * 启用
     */
    enable: boolean;
    constructor(prefix?: string);
    /**
     * 打印消息
     * @param type 类型
     * @param msg 消息
     */
    print(type: string, msg: any): void;
    /**
     * 输出成功信息（绿色）
     * @param msg 文本
     */
    success(msg: any): void;
    /**
     * 输出警告信息（黄色）
     * @param msg 文本
     */
    warning(msg: any): void;
    /**
     * 输出错误信息（红色）
     * @param msg 文本
     */
    error(msg: any): void;
    /**
     * 输出提示信息（蓝色）
     * @param msg 文本
     */
    info(msg: any): void;
}
